INSERT INTO CADOC_ADMIN.PRODUIT_INFO (ISBN, "Date de parution", EAN, "Titre", "Editeur", "Collection", "Prix Fnac", URL, "Resume", "Auteur", "Nombre de pages", "Illustration", "Format", "Poids du produit", "Poids", "Éditeur", "Lien Previsualistion", "Collaborateurs", "Auteurs", "Date de parrution", "Droit de Copier/Coller", "Type de DRM", "Droit d'impression", "Édition", "Collaborateur", "Rédacteur", "Traduit par", "Photos de", "Illustré par", "Original provenant de", "Rédacteurs", "Numérisé", "Impression recto/verso", "Communication sans fil", "Alimentation", "Technologie d'impression", "Hauteur du produit", "Consommation en fonctionnement") VALUES ('9782804441203', null, null, 'Le capitalisme d après', null, null, null, 'C:\Users\Geekzone\Desktop\CC\Cadoc\downloads\9782804441203\1. 9782804441203fs.gif', 'Certains ont qualifié la crise financière de 2007-2009 daccident originel, car elle sapproche du risque systémique qui aurait scellé limplosion de la sphère financière. Cest une crise dune ampleur vertigineuse, profondément traumatisante. Fera-t-elle place à de nouveaux lendemains qui chantent ou exige-t-elle de repenser dès maintenant notre modèle de développement socio-économique ? Bien quils partagent un certain nombre de constats, le banquier Axel Miller et lacadémicien Bruno Colmant ont une vision différente du futur. Le premier invite à repenser le dogme de léconomie de marché - et donc le postulat de la croissance inconditionnelle - car un certain nombre de limites sont atteintes sur le plan territorial, environnemental ou tout simplement humain. Le second voit dans cette crise la fin de létat-providence et le passage brutal à une vitesse supérieure de léconomie du libre-échange, qui va accélérer les ajustements. Dans un cas comme dans lautre, les adaptations prendront du temps et seront difficiles. Avec, à la clé, une hypothèque générationnelle : les jeunes accepteront-ils de rembourser le prix du bien-être que nous nous sommes offerts à crédit ? Pour Bruno Colmant comme pour Axel Miller, nous ne pourrons faire léconomie de politiques nationales et supranationales qui vont en sens inverse de celles qui sont privilégiées aujourdhui. Mais là encore leurs points de vue sont antagonistes. La crise est aussi une épreuve de vérité...', null, '128', null, null, null, null, 'Éditions Larcier, 2010', 'http://books.google.com/books?id=YFQuswEACAAJ&dq=isbn%2B9782804441203&hl=&cd=1&source=gbs_api', null, 'Bruno Colmant  ,   Axel Miller  ,   Chantal Samson', '2010-06-04', null, null, null, null, 'Jean-Hervé Lorenzi', null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO CADOC_ADMIN.PRODUIT_INFO (ISBN, "Date de parution", EAN, "Titre", "Editeur", "Collection", "Prix Fnac", URL, "Resume", "Auteur", "Nombre de pages", "Illustration", "Format", "Poids du produit", "Poids", "Éditeur", "Lien Previsualistion", "Collaborateurs", "Auteurs", "Date de parrution", "Droit de Copier/Coller", "Type de DRM", "Droit d'impression", "Édition", "Collaborateur", "Rédacteur", "Traduit par", "Photos de", "Illustré par", "Original provenant de", "Rédacteurs", "Numérisé", "Impression recto/verso", "Communication sans fil", "Alimentation", "Technologie d'impression", "Hauteur du produit", "Consommation en fonctionnement") VALUES ('9782870161036', null, null, 'Plantation agroforestière d Acacia auriculiformis dans le Haut-Katanga. Guide technique', null, null, null, null, 'L agroforesterie est un mode de gestion durable des sols. Elle associe dans le temps ou dans l espace des arbres avec des cultures et/ou l élevage. Grâce à cette association, des interactions positives se créent entre les différentes composantes du système. Par exemple, l introduction d arbres fixateurs d azote bénéficie aux cultures vivrières en jouant le rôle d engrais vert. L agroforesterie d arbres dans une parcelle de culture apporte également d autres avantages : - ils facilitent la pénétration de l eau dans le sol, - ils permettent de lutter contre l érosion, - ils puisent les éléments nutritifs en profondeur et les rendent disponibles en surface pour les cultures, - ils jouent le rôle de brise-vent, - ils peuvent apporter de l ombrage en saison sèche, - ils peuvent fournir des fruits comestibles, du miel, du bois d oeuvre, du bois de chauffe, des produits médicinaux, etc. L agroforesterie est au coeur du projet "Agroforêts pour le développement de Kipushi", AFODEK, qui a été mis en oeuvre entre décembre 2012 et novembre 2017 grâce au soutien financier de l Union européenne (DCI-FOOD/2012/294-526). S appuyant sur l expérience de la Fondation Hanns Seidel à Mampu (Plateaux Batékés), trois organismes, le GRET, l asbl belge Nature+ et le Centre Promotionnel du Paysannat ont joint leurs efforts pour aménager un périmètre agroforestier de 2000 hectares (ha) dans une zone de savanes dégradées aux sols peu fertiles. Le présent document technique a été élaboré par le projet AFODEK en vue de partager l expérience du périmètre agroforestier de Kipushi. Il s adresse avant tout à un public d agriculteurs ou de techniciens agricoles.', null, '53', null, null, null, null, 'Presses Agronomiques de Gembloux, 2017', 'http://books.google.com/books?id=C64_DwAAQBAJ&printsec=frontcover&dq=isbn%2B9782870161036&hl=&cd=1&source=gbs_api', null, 'Boldrini, Sylvie  ,   Bracke, Charles  ,   Daïnou, Kasso  ,   Vermeulen, Cédric  ,   Fétiveau, Judicaël  ,   Ngoy Shutcha, Mylor  ,   Doucet, Jean-Louis', '2017-09-01', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
