ll, null, null, 'De Boeck Supérieur, 2006', 'http://books.google.com/books?id=21zcswEACAAJ&dq=isbn%2B9782804150143&hl=&cd=1&source=gbs_api', null, ', Christine Marsan', '2006-04-27', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
