ull, null, null, 'Edipro, 2016', 'http://books.google.com/books?id=NejoCwAAQBAJ&pg=PA2&dq=isbn%2B9782874963094&hl=&cd=1&source=gbs_api', 'Jérôme Kervyn de Meerendré ,  Nicolas de Vicq', ', Pierre Guilbert', '2016-02-29', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
