INSERT INTO CADOC_ADMIN.PRODUIT_INFO (ISBN, "Date de parution", EAN, "Titre", "Editeur", "Collection", "Prix Fnac", URL, "Resume", "Auteur", "Nombre de pages", "Illustration", "Format", "Poids du produit", "Poids", "Éditeur", "Lien Previsualistion", "Collaborateurs", "Auteurs", "Date de parrution", "Droit de Copier/Coller", "Type de DRM", "Droit d'impression", "Édition", "Collaborateur", "Rédacteur", "Traduit par", "Photos de", "Illustré par", "Original provenant de", "Rédacteurs", "Numérisé", "Impression recto/verso", "Communication sans fil", "Alimentation", "Technologie d'impression", "Hauteur du produit", "Consommation en fonctionnement") VALUES ('9782804158392', null, null, 'Le B.A.-Ba du management: Guide pratique et roman    Le management en pratique   ,  ISSN   1373-0274', null, null, null, 'C:\Users\Geekzone\Desktop\CC\Cadoc\downloads\9782804158392\1. 9782804158392-g.jpg', 'De la fiction à la réalité... Quatre entreprises aux cultures différentes développent des relations entre elles. Jean, Monique, Maxime, Jean-François, leurs managers, sont des gens comme tout le monde, avec des qualités et des défauts. On suit leurs raisonnements, leurs désillusions, leurs ambitions, leurs réussites et leurs échecs. Et aussi les difficultés quils rencontrent avec leurs collaborateurs, et les tentatives quils lancent pour améliorer les résultats. Au travers de ce fil rouge, qui se lit véritablement comme un roman, rempli de quiproquos et dhumour, lauteur livre sa vision du management. Un management centré sur lHumain, auquel il faut croire plus que jamais. Les nombreuses notes théoriques quont rassemblées nos quatre managers font le tour de toutes les questions que se pose tout cadre au quotidien. Comment motiver ses collaborateurs, comment leur manifester mécontentement ou satisfaction, comment éviter lapparition des divas, comment contrer la résistance au changement, comment sassurer quils aient réellement compris ce quon attend deux ? Mais aussi quel travail le manager doit-il accomplir sur lui-même pour quavec son équipe, il atteigne des résultats à la hauteur de ses ambitions ? Au terme de l ouvrage, Jean, Monique, Maxime et Jean-François auront compris que la plus noble conquête de lHomme reste lui-même. Gageons que le lecteur également. Pourquoi cet ouvrage ? (vidéo de l auteur) Découvrez les vidéos complémentaires au livre Retrouvez les 4 personnages sur Facebook', 'Pierre Guilbert', '256', null, null, null, null, 'De Boeck Supérieur, 2008', 'http://books.google.com/books?id=kf384_w8kiwC&pg=PP258&dq=isbn%2B9782804158392&hl=&cd=1&source=gbs_api', null, ', Pierre Guilbert', '2008-10-02', null, null, null, null, 'Alexandra Borg', null, null, null, null, null, null, null, null, null, null, null, null, null);
